#! /usr/bin/python3
import string

k_p = 0.067
k_r = 0.0385
# 
cipher = ''
cipher = cipher.lower()
dic = [0] * 26
for i in range(26):
    dic[i] = cipher.count(string.ascii_lowercase[i])
dic = [i * (i - 1) for i in dic]
N = len(cipher)
k_0 = float(sum(dic)) / float((N * (N - 1)))
print(k_0)
predict = (k_p - k_r) / (k_0 - k_r)
print(predict)  # length: 8
